mapper.select:
SELECT ps_num, si_image, st_name, pi_image, p_name, p_price, TO_char(ps_expiration ,'yyyy/mm/dd hh24:mi') as ps_expiration, ps_count, ps_price
FROM favstore fs, storeimage si, store st, productsell ps, product p, proimage pi
WHERE c_num = #{c_num} AND fs.st_num = st.st_num AND ps_state = 0 AND st.si_division = si.si_division AND st.s_num = ps.s_num AND ps.p_num = p.p_num AND p.pi_num = pi.pi_num;

SELECT ps_num, si_image, st_name, pi_image, p_name, p_price, TO_char(ps_expiration ,'yyyy/mm/dd hh24:mi') as ps_expiration, ps_count, ps_price
FROM storeimage si, store st, productsell ps, product p, proimage pi 
WHERE ps_state = 0 AND st.si_division = si.si_division AND st.s_num = ps.s_num AND ps.p_num = p.p_num AND p.pi_num = pi.pi_num;
